<?php/* * GENERA LA ESTRUCTURA JSON PARA * LA CARGA DE LAS ESTACIONES * EN EL PANEL IZQUIERDO * @autor christmo * @fecha 2012/02/27 */include('../login/isLogin.php');require_once('../../dll/conect.php');$consultaSql = "    SELECT E.ID_EST,         NOMBRE_EST,         DIRECCION_EST,         FECHA_INSTALACION_EST,         RESPONSABLE_EST,         RESPONSABLE_CONT_EST,         IDEQUIPO_EST,        LON_EST,        LAT_EST,        ALTITUD    FROM usuario_estacion us, estaciones e    WHERE US.ID_EST = E.ID_EST    AND US.ID_US = ".$_SESSION["ID_USER"]."";consulta($consultaSql);$resulsetCoop = variasFilas();$objJson = "[";for ($i = 0; $i < count($resulsetCoop); $i++) {    $fila = $resulsetCoop[$i];    $idEst = $fila["ID_EST"];    $nombreEst = $fila["NOMBRE_EST"];    $objJson .= '{"text":" No. ' . $idEst . ' || ' . $nombreEst . ' ","iconCls":"C' . $idEst . '","id":"' . $idEst . '","leaf":true}'; //, "checked": true    if ($i < (count($resulsetCoop) - 1)) {        $objJson .= ",";    }}$objJson .= "]";echo $objJson;?>